
public class ProcessaMatriz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Como declarar um campo do tipo matriz
		String[] frutas = {"uva","pera","mamão","morango","laranja","goiaba","melância"};
		System.out.println("A nossa primeira fruta é "+frutas[0]);
		System.out.println("A nossa segunda fruta é "+frutas[1]);
		System.out.println("A nossa terceira fruta é "+frutas[2]);
		System.out.println("A nossa quarta fruta é "+frutas[3]);
		System.out.println("A nossa quinta fruta é "+frutas[4]);
		System.out.println("A nossa sexta fruta é "+frutas[5]);
		System.out.println("A nossa sétima fruta é "+frutas[6]);
	}

}
